    //     ? magic.user.getMetadata().then((userData) => {
    //         setUserNormal(userData);
    //       })
    //     : setUserNormal({ user: null });
    // });
  }, []);
  

  return (
    <div className="App">
      <Provider store={configureStore()}>
        <UserContext.Provider value={[userNormal, setUserNormal]}>
          <Routes />
        </UserContext.Provider>
      </Provider>
    </div>
  );
}

export default App;
